import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { usePerlinSettings } from '../hooks/usePerlinSettings';

const FIELD_OF_VIEW = 55;
const CAMERA_START = { x: 0, y: 200, z: 1000 };
const PLANE_SIZE = 5000;
const PLANE_SUBDIVISIONS = 200;
const LINE_COLOR = 0xffffff;
const LINE_OPACITY = 0.02;

function createPerlin() {
  const fade = (t: number) => t * t * t * (t * (t * 6 - 15) + 10);
  const lerp = (a: number, b: number, t: number) => a + t * (b - a);
  const randomGrad = (): [number, number] => {
    const angle = Math.random() * Math.PI * 2;
    return [Math.cos(angle), Math.sin(angle)];
  };

  const GRID_SIZE = 512;
  const gradients: [number, number][][] = [];

  for (let x = 0; x < GRID_SIZE; x++) {
    gradients[x] = [];
    for (let y = 0; y < GRID_SIZE; y++) {
      gradients[x][y] = randomGrad();
    }
  }

  const dot = (ix: number, iy: number, x: number, y: number) => {
    const grad = gradients[ix & (GRID_SIZE - 1)][iy & (GRID_SIZE - 1)];
    return (x - ix) * grad[0] + (y - iy) * grad[1];
  };

  return (x: number, y: number) => {
    const x0 = Math.floor(x), y0 = Math.floor(y);
    const x1 = x0 + 1, y1 = y0 + 1;

    const sx = fade(x - x0);
    const sy = fade(y - y0);

    const ix0 = lerp(dot(x0, y0, x, y), dot(x1, y0, x, y), sx);
    const ix1 = lerp(dot(x0, y1, x, y), dot(x1, y1, x, y), sx);

    return lerp(ix0, ix1, sy);
  };
}

const PerlinBackground: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const settings = usePerlinSettings();
  const settingsRef = useRef(settings);

  // Keep ref up to date to avoid restarting animation frame loop
  useEffect(() => {
    settingsRef.current = settings;
  }, [settings]);

  useEffect(() => {
    if (!containerRef.current) return;

    const noise = createPerlin();
    const scene = new THREE.Scene();

    const camera = new THREE.PerspectiveCamera(FIELD_OF_VIEW, window.innerWidth / window.innerHeight, 1, 5000);
    camera.position.set(CAMERA_START.x, CAMERA_START.y, CAMERA_START.z);

    const renderer = new THREE.WebGLRenderer({ alpha: true, antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    
    // Clean up just in case (e.g. strict mode)
    while (containerRef.current.firstChild) {
      containerRef.current.removeChild(containerRef.current.firstChild);
    }
    containerRef.current.appendChild(renderer.domElement);

    const geometry = new THREE.PlaneGeometry(PLANE_SIZE, PLANE_SIZE, PLANE_SUBDIVISIONS, PLANE_SUBDIVISIONS);
    const material = new THREE.MeshBasicMaterial({ color: LINE_COLOR, wireframe: true, transparent: true, opacity: LINE_OPACITY });

    const mesh = new THREE.Mesh(geometry, material);
    mesh.rotation.x = -(Math.PI / 2.3);
    scene.add(mesh);

    let t = 0, targetX = 0, targetY = 0;

    const handleMouseMove = (e: MouseEvent) => {
      const RANGE = 70;
      targetX = (e.clientX / window.innerWidth - 0.5) * RANGE;
      targetY = (e.clientY / window.innerHeight - 0.5) * RANGE;
    };

    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };

    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('resize', handleResize);

    const pos = geometry.attributes.position;
    let animationFrameId: number;

    const animate = () => {
      const { noiseScale, timeFlow, height } = settingsRef.current;
      t += 0.005;

      for (let i = 0; i < pos.count; i++) {
        const x = pos.getX(i) * 0.01;
        const y = pos.getY(i) * 0.01;
        pos.setZ(i, noise(x * noiseScale + t * timeFlow, y * noiseScale - t * timeFlow) * height);
      }
      pos.needsUpdate = true;

      camera.position.x += (targetX - camera.position.x) * 0.04;
      camera.position.y += (-targetY - camera.position.y) * 0.04;
      camera.lookAt(0, 0, 0);

      renderer.render(scene, camera);
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('resize', handleResize);
      cancelAnimationFrame(animationFrameId);
      geometry.dispose();
      material.dispose();
      renderer.dispose();
      if (containerRef.current && renderer.domElement.parentNode === containerRef.current) {
        containerRef.current.removeChild(renderer.domElement);
      }
    };
  }, []);

  return <div ref={containerRef} className="fixed inset-0 z-0 pointer-events-none" />;
};

export default PerlinBackground;

import React, { useRef, useEffect, useState } from 'react';
import { useTerminal } from '../hooks/useTerminal';
import type { CommandInputHandle } from './CommandInput';
import type { TerminalMode } from '../App';
import { CommandInput } from './CommandInput';
import { OutputDisplay } from './OutputDisplay';
import { Minus, Square, Maximize2, X } from 'lucide-react';
import { Rnd } from 'react-rnd';

interface TerminalProps {
    terminalMode: TerminalMode;
    onMinimize: () => void;
    onMaximize: () => void;
    onClose: () => void;
}

export const Terminal: React.FC<TerminalProps> = ({ terminalMode, onMinimize, onMaximize, onClose }) => {
    const {
        history,
        getPromptPath,
        execute,
        inputHistory,
        activeComponent,
        isInputVisible,
        handleTabCompletion,
        user
    } = useTerminal();

    const inputRef = useRef<CommandInputHandle>(null);
    const scrollContainerRef = useRef<HTMLDivElement>(null);

    const isWindowed = terminalMode === 'windowed';
    const isVisible = terminalMode !== 'hidden' && !activeComponent;

    // Responsive check for Rnd
    const [isMobile, setIsMobile] = useState(typeof window !== 'undefined' ? window.innerWidth < 768 : false);
    
    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth < 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const shouldEnableRnd = isWindowed && !isMobile;

    // Rnd state for windowed mode
    const [rndState, setRndState] = useState(() => ({
        width: Math.min(1024, typeof window !== 'undefined' ? window.innerWidth * 0.8 : 1024),
        height: Math.min(768, typeof window !== 'undefined' ? window.innerHeight * 0.8 : 768),
        x: typeof window !== 'undefined' ? (window.innerWidth - Math.min(1024, window.innerWidth * 0.8)) / 2 : 0,
        y: typeof window !== 'undefined' ? (window.innerHeight - Math.min(768, window.innerHeight * 0.8)) / 2 : 0,
    }));
    const [isInteracting, setIsInteracting] = useState(false);

    // Reset SEO when returning to Desktop/Home
    useEffect(() => {
        if (!activeComponent) {
            document.title = 'Bahauddin Alam - Full Stack Developer';
            const metaDesc = document.querySelector('meta[name="description"]');
            if (metaDesc) metaDesc.setAttribute('content', 'Bahauddin Alam is a Full Stack Developer specializing in React, JavaScript, TypeScript, Tailwind CSS, and Python. Explore his portfolio and projects.');
            
            const canonical = document.querySelector('link[rel="canonical"]');
            if (canonical) canonical.setAttribute('href', 'https://bahauddin.in');
        }
    }, [activeComponent]);

    // Auto-scroll on ANY content change
    useEffect(() => {
        const container = scrollContainerRef.current;
        if (!container) return;

        const scrollToBottom = () => {
            requestAnimationFrame(() => {
                container.scrollTop = container.scrollHeight;
            });
        };

        // Scroll immediately on history change
        scrollToBottom();

        // Also observe DOM mutations for components that update internally (e.g. Ping)
        const observer = new MutationObserver(scrollToBottom);
        observer.observe(container, { childList: true, subtree: true });

        return () => observer.disconnect();
    }, [history]);

    // Refocus input when activeComponent closes or terminal becomes visible
    useEffect(() => {
        if (!activeComponent && terminalMode !== 'hidden') {
            setTimeout(() => {
                inputRef.current?.focus();
            }, 10);
        }
    }, [activeComponent, terminalMode]);

    const handleContainerClick = () => {
        // Don't focus if user is selecting text
        const selection = window.getSelection();
        if (selection && selection.toString().length > 0) return;

        inputRef.current?.focus();
    };

    const terminalContent = (
        <div className={`w-full h-full flex flex-col overflow-hidden ${isWindowed ? 'rounded-lg' : ''} bg-elegant-bg transition-transform duration-500 origin-center ${
            !shouldEnableRnd && isWindowed ? 'shadow-2xl shadow-black/80 border border-elegant-border' : ''
        } ${
            !isVisible ? 'scale-95' : 'scale-100 ease-[cubic-bezier(0.34,1.56,0.64,1)]'
        }`}>
            {/* Top Bar - KDE / Linux Style */}
            <div className={`w-full h-10 bg-elegant-card flex items-center px-4 justify-end border-b border-elegant-border flex-shrink-0 select-none relative z-20 ${
                shouldEnableRnd ? 'cursor-move terminal-drag-handle' : 'cursor-default'
            }`}>
                <div className="absolute left-4 transform-none md:left-1/2 md:-translate-x-1/2 text-gray-400 text-lg font-medium font-mono truncate max-w-[60%] md:max-w-none pointer-events-none">
                    {user}@neosphere:~
                </div>
                <div className="flex gap-3 relative z-10 pointer-events-auto">
                    <div
                        onClick={(e) => { e.stopPropagation(); onMinimize(); }}
                        className="p-1.5 hover:bg-white/10 rounded-full transition-colors cursor-pointer text-gray-500 hover:text-white"
                        title="Minimize"
                    >
                        <Minus size={14} />
                    </div>
                    <div
                        onClick={(e) => { e.stopPropagation(); isWindowed ? onMaximize() : onMinimize(); }}
                        className="p-1.5 hover:bg-white/10 rounded-full transition-colors cursor-pointer text-gray-500 hover:text-white"
                        title={isWindowed ? 'Maximize' : 'Restore'}
                    >
                        {isWindowed ? <Square size={12} /> : <Maximize2 size={12} />}
                    </div>
                    <div
                        onClick={(e) => { e.stopPropagation(); onClose(); }}
                        className="p-1.5 hover:bg-red-500 hover:text-white rounded-full transition-colors cursor-pointer text-gray-500"
                        title="Close"
                    >
                        <X size={14} />
                    </div>
                </div>
            </div>

            {/* Scrollable Terminal Content */}
            <div
                ref={scrollContainerRef}
                className="flex-1 min-h-0 p-4 overflow-y-auto font-mono text-lg font-medium cursor-text"
                onClick={handleContainerClick}
            >
                <div className="max-w-5xl mx-auto">
                    <OutputDisplay history={history} />
                    {isInputVisible && (
                        <CommandInput
                            ref={inputRef}
                            promptPath={getPromptPath()}
                            user={user}
                            onSubmit={execute}
                            inputHistory={inputHistory}
                            onTabComplete={handleTabCompletion} />
                    )}
                </div>
            </div>
        </div>
    );

    return (
        <>
            {/* Full Screen Component Layer - always rendered independently */}
            {activeComponent && (
                <div className="fixed inset-0 z-50 bg-black/40 backdrop-blur-sm text-elegant-text-primary font-mono text-base p-4 overflow-hidden animate-in fade-in duration-300">
                    {activeComponent}
                </div>
            )}

            {/* Terminal Window Wrapper */}
            <div className={`fixed z-30 transition-all duration-500 ease-[cubic-bezier(0.34,1.56,0.64,1)] pointer-events-none ${
                !isVisible
                    ? 'opacity-0 invisible scale-95 origin-bottom'
                    : 'opacity-100 scale-100 origin-center'
            } ${
                !shouldEnableRnd && isWindowed
                    ? 'inset-3 md:inset-[8%] lg:inset-x-[12%] lg:inset-y-[8%]'
                    : 'inset-0'
            }`}>
                {shouldEnableRnd ? (
                    <Rnd
                        className={`${isVisible ? 'pointer-events-auto' : 'pointer-events-none'} ${!isInteracting ? 'transition-all duration-500 ease-[cubic-bezier(0.34,1.56,0.64,1)] shadow-2xl shadow-black/80 border border-elegant-border rounded-lg' : 'shadow-2xl shadow-black/80 border border-elegant-border rounded-lg'}`}
                        size={{ width: rndState.width, height: rndState.height }}
                        position={{ x: rndState.x, y: rndState.y }}
                        onDragStart={() => setIsInteracting(true)}
                        onDragStop={(_e, d) => {
                            setIsInteracting(false);
                            setRndState(prev => ({ ...prev, x: d.x, y: d.y }));
                        }}
                        onResizeStart={() => setIsInteracting(true)}
                        onResizeStop={(_e, _direction, ref, _delta, position) => {
                            setIsInteracting(false);
                            setRndState({
                                width: parseFloat(ref.style.width) || ref.offsetWidth,
                                height: parseFloat(ref.style.height) || ref.offsetHeight,
                                ...position,
                            });
                        }}
                        bounds="window"
                        minWidth={350}
                        minHeight={250}
                        dragHandleClassName="terminal-drag-handle"
                    >
                        {terminalContent}
                    </Rnd>
                ) : (
                    <div className="w-full h-full pointer-events-auto">
                        {terminalContent}
                    </div>
                )}
            </div>
        </>
    );
};

